import React,{useState,useEffect}from 'react';
import img from "./img/plane.jpg"

function useMouse(){
    const [x, setX] = useState(0)
    const [y, setY] = useState(0)

    handleMouseMove = e =>{
      setX(e.clientX)
      setY(e.clientY)
    } 
}
