import React, { Component } from 'react'

export default class Footer extends Component {
    render() {
        const {todos} = this.props
        const doneCount = todos.reduce((pre,current)=>pre+(current.done?1:0),0)
        const total=todos.length

        handleAll = ()=>{
            this.props.checkAllTodo()
        }

        return (
            <div>
                <label>
                    <input type="checkbox" checked={doneCount===total} onChange={this.handleAll()}>
                    </input>
                </label>
                <span>
                    <span>
                        已完成{doneCount}
                    </span>
                    /全部 {total}
                </span>
                <button>清除已完成的任务</button>
            </div>
        )
    }
}
