import React, { Component } from 'react';
import Header from "./component/Header/Header"
import List from "./component/List/List"
import Footer from "./component//Footer/Footer"
import './App.css'

//App是外壳 将其内部需要渲染的东西全都写为组件

class App extends Component{

  //状态在哪里 操作状态的方法就在哪里
  state={
    todos:[{
      id:"001",
      name:"吃饭1",
      done:true  
    },{
      id:"002",
      name:"吃饭2",
      done:true  
    },{
      id:"003",
      name:"吃饭3",
      done:false  
    },{
      id:"004",
      name:"吃饭4",
      done:false  
    }]
  }

  changeState = (msg) => {//msg是个todo obj
    const newTodos = [msg,...this.state.todos]
    this.setState({
      todos:newTodos
    })
   /*  this.setState({
      todos:this.state.todos.unshift(msg) 返回的是个长度 
    }) */
  }

  changeTodo = (id,flag) => {
    const {todos} = this.state
    const newTodos=todos.map((todoObj) => {
      if(todoObj.id===id)return{...todoObj,done:flag}
      else return todoObj
      /* {
        todoObj.done=flag 不可以直接修改state！
      } */
    })
    this.setState({
      todos:newTodos
    })
  }

  deleteTodo = (id)=>{
    const {todos} = this.state
    const newTodos = todos.filter((todoObj)=>{
      return todoObj.id!==id
    })
    this.setState({
      todos:newTodos
    })
  }

  checkAllTodo = (done)=>{
    const {todos} = this.state
    const newTodos = todos.map((todoObj)=>{
      return {...todoObj,done}
    })
    this.setState({
      todos:newTodos
    })
  }

  clearDoneTodo = (id)=>{
    const {todos} = this.state
    const newTodos = todos.filter((todoObj)=>{
      return id.includes(odoObj.id)
    })
  }

  render(){
    return(
      <div className="app">
        <Header changeState={this.changeState}/>
        <List todos={this.state.todos} changeTodo={this.changeTodo} deleteTodo={this.deleteTodo}/>
        <Footer todos={this.state.todos} checkAllTodo={this.checkAllTodo}/>
      </div>
    )
  }
}

export default App;
