import React, { Component } from 'react'

export default class Count extends Component {
    render() {
        return (
            <div>
                <h1>当前求和为：{this.selectName}</h1>
                <select
                ref={c=>this.selectName=c}>
                <option value="1">1</option>
                <option value="2">2</option>
                <option value="3">3</option>
                </select>{/* 参数c即是这个标签对象 */}
                <button>+</button>  
                <button>-</button>
                <button>当前和为奇数时加</button>
                <button>异步加</button>
            </div>
        )
    }
}
