import CountUI from '../../components/Count/Count'
import {connect} from 'react-redux'
import store from '../../redux/store';


//a函数的返回值作为状态传递给了UI组件
function a(state){
    return {count:state}
}

//b函数的返回值作为操作状态的方法传递给UI组件
function b(dispatch){
    return {jia:(number)=>{
        dispatch({type:"increment",data:number});
    }}
}

export default connect(a,b)(CountUI)
