import React, { Component } from 'react'
import '../index.css'
export default class Square extends Component {
    constructor(props){
        super(props)
        this.handleClick=this.handleClick.bind(this)
    }
    state={
        value:"X"
    }
    handleClick=()=>{
       if(this.state==="O"){
        this.setState({
            value:"X"
        })
       }else{
        this.setState({
            value:"O"
        })
       }
    }
    render() {
        return (
            <button className="square" onClick={()=>this.handleClick}>
                {this.state.value}
            </button>
        )
    }
}
